event.executesql:
USE master;
declare @Message varchar(255), @DateDone DateTime;
declare @WeekDay varchar(50),@CurrentDate varchar(50),@Subject varchar(200)

Select @DateDone = GetDate()
Set @Message = 'BANKERSTOOLBOXDB: Archon Job: Bankers Toolbox Run Daemon Completely Finished on:..' + FORMAT(@DateDone, 'F') + '.';
Select @Message
set @Subject = 'BANKERSTOOLBOXDB: Archon Job: Bankers Toolbox Run Daemon Completely Finished on:..' + FORMAT(@DateDone, 'F') + '.';


